mapper.select:
SELECT t.order_id orderId, t.payment, t.`status`, t.item_num itemNum FROM tb_order t
t.user_id = #{USERID}
AND t.status = #{STATUS}
ORDER BY t.create_time DESC

mapper.update:
        UPDATE tb_order t
		SET t.STATUS = #{STATUS}

		   ,t.payment_time = #{PAYMENTTIME}

		WHERE
			t.order_id = #{ORDERID}
